document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("searchInput"),t=document.getElementById("searchIcon"),n=document.querySelectorAll(".event-card");function o(){const t=e.value.toLowerCase().trim();n.forEach((e=>{const n=e.querySelector(".event-content h2").textContent.toLowerCase();""===t||n.includes(t)?e.style.display="":e.style.display="none"}))}t.addEventListener("click",(function(e){e.preventDefault(),o()})),e.addEventListener("keypress",(function(e){"Enter"===e.key&&(e.preventDefault(),o())})),e.addEventListener("input",(function(){""===e.value.trim()&&n.forEach((e=>{e.style.display=""}))}))}));